var path = require('path');
var fs = require('fs');

var phpfmt = {
  pharName: 'fmt.stub.php',
  installUrl: 'https://raw.githubusercontent.com/driade/phpfmt8/master/fmt.stub.php'
};
phpfmt.pharPath = path.join(__dirname, phpfmt.pharName);

phpfmt.hasInstalled = function () {
  return fs.existsSync(phpfmt.pharPath);
};

phpfmt.default = phpfmt;

module.exports = exports = phpfmt;
